import { useCvRuslanContext } from "../../../context/CvRuslanContext";
import CvPortfolioAbout from "./CvPortfolioAbout";
import CvPortfolioImage from "./CvPortfolioImage";

const CvPortfolioProjects = () => {
  const { isMobile } = useCvRuslanContext();

  const onClickProveSize = () => {
    console.log(window.screenX);
  };

  return (
    <>
      <h2>My Projects</h2>
      <div className="bg-orange-100 rounded-lg px-4 py-8 flex flex-col items-center">
        <section className="outline grid sm:grid-cols-2 sm:grid-rows-1 grid-rows-2 w-full rounded-2xl py-4 px-8 relative">
          <CvPortfolioImage />
          <CvPortfolioAbout />
          <button
            onClick={onClickProveSize}
            className="aspect-square grid place-items-center rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[-50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400"
          >
            <img src="/img/pluggins/arrow.svg" alt="Arrow left" className="rotate-180" />
          </button>
          <button className="aspect-square grid place-items-center rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400 right-0">
            <img src="/img/pluggins/arrow.svg" alt="Arrow rigth" />
          </button>
        </section>
      </div>
    </>
  );
};

export default CvPortfolioProjects;
