import CvPortfolioAbout from "./CvPortfolioAbout";
import CvPortfolioImage from "./CvPortfolioImage";

const CvPortfolioProjects = () => {
  return (
    <>
      <h2>My Projects</h2>
      <div className="bg-orange-100 rounded-lg px-4 py-8 flex flex-col items-center">
        <h3>NAME OF PROJECT</h3>
        <section className="outline grid sm:grid-cols-2 sm:grid-rows-1 grid-rows-2 w-full rounded-2xl py-4 px-8 relative">
          <CvPortfolioImage />
          <CvPortfolioAbout />
          <button className="aspect-square rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[-50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400">
            <img src="/img/plugging/arrow.svg" alt="Arrow left" />
          </button>
          <button className="aspect-square rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400 right-0">
            <img src="/img/plugging/arrow.svg" alt="Arrow rigth" />
          </button>
        </section>
      </div>
    </>
  );
};

export default CvPortfolioProjects;
