import { useState } from "react";
import { useCvRuslanContext } from "../../../context/CvRuslanContext";
import CvPortfolioAbout from "./CvPortfolioAbout";
import CvPortfolioImage from "./CvPortfolioImage";

const CvPortfolioProjects = () => {
  const { aboutProjectRef, portfolioChecking, setPortfolioChecking, projectsContainer } = useCvRuslanContext();
  const [isClicked, setIsClicked] = useState<boolean>(false);

  const animationChange = () => {
    if (isClicked === false) {
      const aboutProject = aboutProjectRef.current;

      setIsClicked(true);
      aboutProject?.classList.add("animate-startPortfolio");
      aboutProject?.addEventListener(
        "animationend",
        () => {
          aboutProject?.classList.remove("animate-startPortfolio");
          setIsClicked(false);
        },
        { once: true }
      );
    } else return;
  };

  const onClickLeft = () => {
    if (portfolioChecking.projectNum === 1) {
      setPortfolioChecking((pr) => ({ ...pr, projectNum: projectsContainer.length }));
    } else {
      setPortfolioChecking((pr) => ({ ...pr, projectNum: pr.projectNum - 1 }));
    }
  };
  const onClickRight = () => {
    if (portfolioChecking.projectNum === projectsContainer.length) {
      setPortfolioChecking((pr) => ({ ...pr, projectNum: 1 }));
    } else {
      setPortfolioChecking((pr) => ({ ...pr, projectNum: pr.projectNum + 1 }));
    }
  };

  return (
    <>
      <h2>My Projects</h2>
      <div className="bg-orange-100 rounded-lg px-4 py-8 flex flex-col items-center">
        <section className="grid sm:grid-cols-2 sm:grid-rows-1 grid-rows-2 w-full rounded-2xl md:py-4 md:px-8 py-2 px-4 relative">
          <CvPortfolioImage />
          <CvPortfolioAbout />
          <button
            onClick={onClickLeft}
            className="aspect-square grid place-items-center rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[-50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400"
          >
            <img src="/img/pluggins/arrow.svg" alt="Arrow left" className="rotate-180" />
          </button>
          <button
            onClick={onClickRight}
            className="aspect-square grid place-items-center rounded-md bg-orange-200 h-7 absolute top-2/4 translate-y-[-50%] hover:translate-y-[calc(-50%-0.25rem)] translate-x-[50%] duration-150 hover:shadow-buttonShadow hover:bg-orange-400 right-0"
          >
            <img src="/img/pluggins/arrow.svg" alt="Arrow rigth" />
          </button>
        </section>
      </div>
    </>
  );
};

export default CvPortfolioProjects;
