const CvHeader = () => {
  return (
    <header className="font-urbanist flex flex-row w-full items-center justify-between px-12 py-6 mb-12 bg-gradient-to-r from-indigo-300 from-10% via-sky-300 via-50% to-emerald-300 to-80% rounded-b-[2rem]">
      <div>
        <h1 className="text-3xl font-bold lg:text-5xl 2xl:text-6xl">Karaniewski Rus≈Çan</h1>
        <p className="text-xl lg:text-2xl 2xl:text-3xl">Frontend developer</p>
      </div>
      <div className="absolute h-44 lg:h-48 2xl:h-56 top-0 right-0 mx-12 my-6 overflow-hidden aspect-square rounded-full hover:cursor-pointer">
        <img
          src="/img/00_header/Ruslan_Karaneuski.png"
          className=" h-44 lg:h-48 2xl:h-56 top-0 right-0 hover:scale-110 duration-300 ease"
          alt=""
        />
      </div>
    </header>
  );
};

export default CvHeader;
