import { useCvRuslanContext } from "../context/CvRuslanContext";

const CvButtonScreenUp = () => {
  const { isMobile } = useCvRuslanContext();

  const onClickToTop = () => {
    window.scrollY = 0;
  };

  return (
    <div
      onClick={onClickToTop}
      className={`duration-500 ${
        isMobile ? "opacity-100" : "opacity-0"
      } cursor-pointer aspect-square grid place-items-center fixed w-12 rounded-2xl bg-orange-300 bottom-8 right-8`}
    >
      <img src="/img/pluggins/screenUp.svg" alt="" className=" w-full" />
    </div>
  );
};

export default CvButtonScreenUp;
