import { useCvRuslanContext } from "../../context/CvRuslanContext";

const CvNavigation = () => {
  const { navpanelRef } = useCvRuslanContext();

  console.log(navpanelRef.current?.getBoundingClientRect());

  return (
    <nav
      ref={navpanelRef}
      className="font-urbanist flex justify-end px-8 opacity-0 fixed hover:opacity-100 top-0 right-0 w-full duration-150 bg-orange-200"
    >
      <ul className=" list-none flex flex-row gap-8">
        <li className=" cursor-pointer ease-in-out duration-300 hover:scale-105 hover:font-bold">About</li>
        <li className=" cursor-pointer ease-in-out duration-300 hover:scale-105 hover:font-bold">Porfolio</li>
        <li className=" cursor-pointer ease-in-out duration-300 hover:scale-105 hover:font-bold">Contact</li>
      </ul>
    </nav>
  );
};

export default CvNavigation;
