import { useState } from "react";
import { useCvRuslanContext } from "../../context/CvRuslanContext";
import { Link } from "react-router-dom";

const CvNavigation = () => {
  const { navpanelRef } = useCvRuslanContext();
  const [isMobile, setIsMobile] = useState<boolean>(false);

  const onSizeChange = () => {
    if (window.innerWidth < 768) {
      setIsMobile(true);
    } else {
      setIsMobile(false);
    }
  };

  return (
    <nav
      ref={navpanelRef}
      className="flex justify-end px-8 opacity-0 fixed hover:opacity-100 top-0 right-0 w-full duration-300 bg-orange-100 border-b-2 border-orange-200"
    >
      <ul className=" list-none flex flex-row gap-8">
        <li className=" cursor-pointer ease-in-out duration-150 hover:scale-105 font-bold text-gray-700 hover:text-gray-950">
          <Link to="/">About</Link>
        </li>
        <li className=" cursor-pointer ease-in-out duration-150 hover:scale-105 font-bold text-gray-700 hover:text-gray-950">
          <Link to="/karaniewskiportfolio">Portfolio</Link>
        </li>
        <li className=" cursor-pointer ease-in-out duration-150 hover:scale-105 font-bold text-gray-700 hover:text-gray-950">
          <Link to="/karaniewskicontact">Contact</Link>
        </li>
      </ul>
    </nav>
  );
};

export default CvNavigation;
