import { useCvRuslanContext } from "../../../context/CvRuslanContext";

const CvPortfolioImage = () => {
  const { radioID } = useCvRuslanContext();

  return (
    <div className=" aspect-project py-2 px-4 relative">
      <img src="\img\02_portfolio\testimg.png " alt="" className=" rounded-lg" />
      <form action="" className="absolute w-fit bottom-0 right-1/2 translate-x-1/2 flex gap-2">
        <label htmlFor="">
          <input
            type="radio"
            id={`${radioID}_1`}
            name="imageNum"
            className="cursor-pointer bg-zinc-300 appearance-none w-4 aspect-square rounded-full checked:bg-zinc-600 outline outline-1 outline-black before:w-4 before:aspect-square before:block before:bg-slate-700"
            checked
          />
        </label>
        <label htmlFor="">
          <input
            type="radio"
            id={`${radioID}_2`}
            name="imageNum"
            className="cursor-pointer bg-zinc-300 appearance-none w-4 aspect-square rounded-full checked:bg-zinc-600 outline outline-1 outline-black before:w-4 before:aspect-square before:block  before:bg-slate-700"
          />
        </label>
        <label htmlFor="">
          <input
            type="radio"
            id={`${radioID}_3`}
            name="imageNum"
            className="cursor-pointer bg-zinc-300 appearance-none w-4 aspect-square rounded-full checked:bg-zinc-600 outline outline-1 outline-black before:w-4 before:aspect-square before:block before:bg-slate-700"
          />
        </label>
      </form>
    </div>
  );
};

export default CvPortfolioImage;
