import { useCvRuslanContext } from "../../../context/CvRuslanContext";

const CvPortfolioAbout = () => {
  const { projectsContainer, portfolioChecking } = useCvRuslanContext();

  const cocoJumbo = projectsContainer.find((prj) => prj.id === portfolioChecking.projectNum)!;

  return (
    <div className=" aspect-project py-2 px-4 flex flex-col gap-2 relative">
      <h3 className="text-left">{cocoJumbo.nameOfProject}</h3>
      <p data-with-indent>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Porro id quis blanditiis, error necessitatibus minus
        laboriosam soluta, eum provident explicabo, harum tempore ullam maiores! Magni minima molestias aliquam placeat
        distinctio.
      </p>
      <div className="flex gap-4 justify-center absolute bottom-0 right-1/2 translate-x-1/2 translate-y-1/2 w-max">
        <button className="bg-white px-2 py-1 rounded-lg duration-150 hover:translate-y-[-0.25rem] hover:shadow-buttonShadow hover:text-white hover:bg-rose-300">
          <a href={`www`}>Life page</a>
        </button>
        <button className="bg-white px-2 py-1 rounded-lg duration-150 hover:translate-y-[-0.25rem] hover:shadow-buttonShadow hover:text-white hover:bg-rose-300">
          <a href={`www`}>Repository</a>
        </button>
      </div>
    </div>
  );
};

export default CvPortfolioAbout;
